import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// ðŸ”¹ Rasmlar
import buyumOynasi from "../../assets/5.1/buyum_oynasi.png";
import pichan from "../../assets/7.6/pichan_ivitmasi-removebg-preview.png";
import paxta from "../../assets/7.6/paxta_tolasi-removebg-preview.png";
import pinset from "../../assets/5.1/pinset_o_zi.png";
import pipetka from "../../assets/5.1/pipetka_jpg-removebg-preview.png";
import qaychi from "../../assets/7.6/qaychi-removebg-preview.png";
import qoplagich from "../../assets/5.1/qoplagich_jpg-removebg-preview.png";
import filter from "../../assets/7.6/filtr_qg-removebg-preview.png";
import mik from "../../assets/7.6/mikroskop-removebg-preview.png";

const steps = [
  "Idishdagi iliq suvga shakar soling.",
  "Shakarli suvga achitqi zamburug'idan soling va iliq joyga qo'ying.",
  "Ko'pchib chiqqan xamirtuturshtan pipetka yordamida oling.",
  "Buyum oynasiga tomizing.",
  "Ustidan qoplagich oynani yoping.",
  "Tayyor bo'lgan preparatni mikroskop ostida kuzating.",
];
const stepsData = [
  {
    text: "Idishdagi iliq suvga shakar soling.",
    img1: piyoz,
    img2: skalpelN,
    resultImg: piozP,
  },
  {
    text: "Piyozni skalpel yordamida bo'ling",
    img1: suvStakan,
    img2: pipetka,
    resultImg: pipS,
  },
  {
    text: "Pipetka yordamida idishdagi suvdan oling.",
    img1: buyumOynasi,
    img2: pipS,
    resultImg: suvliBuyumOna,
  },
  {
    text: " Tozalab artilgan buyum oynasiga pipetka yordamida bir tomchi suv tomizing.",
    img1: piozP,
    img2: pinset,
    resultImg: pinDaPiyoz,
  },
  {
    text: " Pinset yordamida kesilgan piyozning yupqa po'stini ajratib oling.",
    img1: suvliBuyumOna,
    img2: pinDaPiyoz,
    resultImg: suvliBuyumOna,
  },
  {
    text: " Piyozning yupqa po'stini buyum oynasidagi bir tomchi suv ustiga tekis qilib joylashtiring",
    img1: yod,
    img2: pipetka,
    resultImg: yPipetka,
  },
  {
    text: " Pipetka yordamida yod eritmasidan oling.",
    img1: suvliBuyumOna,
    img2: yPipetka,
    resultImg: buyumY,
  },
  {
    text: " Piyozning yupqa po'stini buyum oynasiga qo'ygandan so'ng, ustiga 1 tomchi yod eritmasini tomizing",
    img1: buyumY,
    img2: qoplagich,
    resultImg: buyumQopOyna,
  },
  {
    text: "Qoplagich oyna bilan yoping.",
    img1: buyumQopOyna,
    img2: filterQ,
    resultImg: buyumQopOyna,
  },
  {
    text: "Tayyor bo'lgan preperatni mikroskop ostida kuzating.",
    img1: mikroskop,
    img2: buyumQopOyna,
    resultImg: underMik ,
  },
];


const Tufelka = () => {
   const navigate = useNavigate();
    const [openIndex, setOpenIndex] = useState(null);
    const [activeIndex, setActiveIndex] = useState(null);
    const [animate, setAnimate] = useState(false);
    const [showResult, setShowResult] = useState(false);

    // Accordion toggle: faqat bittasi ochiq boâ€˜lsin
    const toggle = (index) => {
      setOpenIndex(openIndex === index ? null : index);
      setAnimate(false);
      setShowResult(false);
    };

    // "Ishni bajarish" tugmasi
    const handleDoClick = (index) => {
      setActiveIndex(index);
      setAnimate(true);
      setTimeout(() => setShowResult(true), 1500);
    };

    // "Yangilash" tugmasi
    const handleRefresh = () => {
      setAnimate(false);
      setShowResult(false);
    };

  return (
    <div data-aos="fade-down" className="tufelka-container">
      <div className="max-width">
        {/* ðŸ”¹ Sarlavha qismi */}
        <div className="pages-title">
          <h1>O'simliklarda moddalar transportini o'rganish</h1>
          <div onClick={() => navigate(-1)} className="display-flex orqaga">
            <i className="bx bx-chevron-left"></i>
            <p>Orqaga</p>
          </div>
        </div>

        {/* ðŸ”¹ Yon panel (asboblar) */}
        <div className="side-bar">
          {[buyumOynasi, pichan, paxta, pinset, pipetka, qaychi, qoplagich, filter, mik].map(
            (img, i) => (
              <div className="bar-img" key={i}>
                <img src={img} alt={`asbob-${i}`} />
              </div>
            )
          )}
        </div>



        {/* ðŸ”¹ Asosiy qism â€” Accordion */}
         <div className="accordion-container">
          {stepsData.map((step, index) => (
            <div key={index} className="accordion-item">
              <button
                onClick={() => toggle(index)}
                className={`accordion-header ${openIndex === index ? "active" : ""}`}
              >
                <span>
                  {index + 1}. {step.text}
                </span>
                <span className="arrow">{openIndex === index ? "â–¾" : "â–¸"}</span>
              </button>

              {openIndex === index && (
                <div className={`accordion-content ac-${index + 1}`}>
                  <div className={`animation-area ${animate && activeIndex === index ? "active" : ""}`}>
                    {!showResult ? (
                      <>
                        {step.img1 && <img src={step.img1} alt="img1" className="left-img" />}
                        {step.img2 && <img src={step.img2} alt="img2" className="right-img" />}
                      </>
                    ) : (
                      step.resultImg && <img src={step.resultImg} alt="result" className="result-img" />
                    )}
                    {/* <img className="img-10" src={res} alt="bg" /> */}
                  </div>

                  <div className="buttons">
                    <button className="do-btn zambrug-cont-btn" onClick={() => handleDoClick(index)}>
                      Ishni bajarish
                    </button>
                    <button className="refresh-btn zambrug-cont-btn" onClick={handleRefresh}>
                      Yangilash
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tufelka;
